/* @flow */

import Dep from './dep'
import VNode from '../vdom/vnode'
import { arrayMethods } from './array'
import {
  def,
  warn,
  hasOwn,
  hasProto,
  isObject,
  isPlainObject,
  isPrimitive,
  isUndef,
  isValidArrayIndex,
  isServerRendering
} from '../util/index'

const arrayKeys = Object.getOwnPropertyNames(arrayMethods)

/**
 * In some cases we may want to disable observation inside a component's
 * update computation.
 */
export let shouldObserve: boolean = true

export function toggleObserving (value: boolean) {
  shouldObserve = value
}

/**
 * Observer class that is attached to each observed
 * object. Once attached, the observer converts the target
 * object's property keys into getter/setters that
 * collect dependencies and dispatch updates.
 */
export class Observer {
  value: any;
  dep: Dep;
  vmCount: number; // number of vms that have this object as root $data

  constructor (value: any) {
    // Observerå®ä¾‹å…·æœ‰3ä¸ªå±æ€§ï¼Œåˆ†åˆ«ä¸ºvalueï¼Œdep, vmCount
    this.value = value
    this.dep = new Dep()
    this.vmCount = 0
    def(value, '__ob__', this)  //ğŸ‘ˆ å°†Obseverå®ä¾‹æŒ‚åœ¨åˆ°ç›‘å¬æ•°æ®çš„__ob__å±æ€§ä¸Š
    if (Array.isArray(value)) {
      if (hasProto) { // æµè§ˆå™¨æ”¯æŒ__proto__, å°†æ‹¦æˆªå™¨æŒ‚è½½åˆ°æ•°ç»„çš„__proto__ä¸Šã€‚æ‹¦æˆªå™¨çš„__proto__æŒ‡å‘Array.prototype
        protoAugment(value, arrayMethods)
      } else {  // ä¸æ”¯æŒ__proto__, å°†æ‹¦æˆªå™¨ä¸­çš„æ–¹æ³•æŒ‚è½½åˆ°æ•°ç»„ä¸Š
        copyAugment(value, arrayMethods, arrayKeys)
      }
      this.observeArray(value)
    } else {                       // ğŸ‘ˆ ä¸æ˜¯æ•°ç»„ï¼Œèµ°è¿™ä¸€æ­¥
      this.walk(value)            //ğŸ‘ˆ æ‰§è¡Œwalkæ–¹æ³•
    }
  }

  /**
   * Walk through all properties and convert them into
   * getter/setters. This method should only be called when
   * value type is Object.
   */
  walk (obj: Object) {
    const keys = Object.keys(obj)
    for (let i = 0; i < keys.length; i++) {
      defineReactive(obj, keys[i])
    }
  }

  /**
   * Observe a list of Array items.
   */
  observeArray (items: Array<any>) {
    for (let i = 0, l = items.length; i < l; i++) {
      observe(items[i])
    }
  }
}

// helpers

/**
 * Augment a target Object or Array by intercepting
 * the prototype chain using __proto__
 */
function protoAugment (target, src: Object) {
  /* eslint-disable no-proto */
  target.__proto__ = src
  /* eslint-enable no-proto */
}

/**
 * Augment a target Object or Array by defining
 * hidden properties.
 */
/* istanbul ignore next */
function copyAugment (target: Object, src: Object, keys: Array<string>) {
  for (let i = 0, l = keys.length; i < l; i++) {
    const key = keys[i]
    def(target, key, src[key])
  }
}

/**
 * Attempt to create an observer instance for a value,
 * returns the new observer if successfully observed,
 * or the existing observer if the value already has one.
 */
export function observe (value: any, asRootData: ?boolean): Observer | void {
  if (!isObject(value) || value instanceof VNode) {  // ğŸ‘ˆ å¦‚æœä¸æ˜¯å¼•ç”¨ç±»å‹ï¼Œæˆ–è€…æ˜¯VNodeåˆ™é€€å‡ºå‡½æ•°ï¼ˆé€’å½’ç»“æŸçš„æ¡ä»¶ï¼‰ã€‚
    return
  }
  let ob: Observer | void
  if (hasOwn(value, '__ob__') && value.__ob__ instanceof Observer) {   // ğŸ‘ˆ å¦‚æœè¯¥å€¼å·²ç»æ‹¥æœ‰äº†__ob__å±æ€§ï¼Œåˆ™å·²ç»å“åº”å¼åŒ–äº†ã€‚
    ob = value.__ob__
  } else if (
    shouldObserve &&
    !isServerRendering() &&
    (Array.isArray(value) || isPlainObject(value)) && //ğŸ‘ˆ æ˜¯æ™®é€šå¯¹è±¡æˆ–æ•°ç»„ isPlainObjectçš„æ ¸å¿ƒè¯­æ³•æ˜¯ Object.prototype.toString.call(obj) === '[object Object]'
    Object.isExtensible(value) &&  // ğŸ‘ˆ Object.isExtensibleç”¨äºåˆ¤æ–­ä¸€ä¸ªå¯¹è±¡æ˜¯å¦å¯æ·»åŠ æ–°çš„å±æ€§
    !value._isVue
  ) {
    ob = new Observer(value)  //ğŸ‘ˆ åˆå§‹åŒ–ç»„ä»¶æ—¶æ‰§è¡Œäº†observe(data);
  }
  if (asRootData && ob) {
    ob.vmCount++
  }
  return ob
}

/**
 * Define a reactive property on an Object.
 */
export function defineReactive (
  obj: Object,
  key: string,
  val: any,
  customSetter?: ?Function,
  shallow?: boolean
) {
  const dep = new Dep()  //ğŸ‘ˆ é¦–å…ˆä¸ºdataçš„æ¯ä¸ªå±æ€§æˆå‘˜æ–°å»ºä¸€ä¸ªdep

  const property = Object.getOwnPropertyDescriptor(obj, key)
  if (property && property.configurable === false) {  //ğŸ‘ˆ å¦‚æœè¯¥å¯¹è±¡çš„è¯¥å±æ€§çš„æè¿°å­˜åœ¨ï¼Œä¸”ä¸å¯ä»¥é…ç½®ï¼Œåˆ™é€€å‡ºå‡½æ•°ã€‚
    return
  }

  // cater for pre-defined getter/setters
  const getter = property && property.get
  const setter = property && property.set
  if ((!getter || setter) && arguments.length === 2) {  //ğŸ‘ˆ (æ²¡æœ‰getter æˆ– æœ‰getterä½†èµ·ç æœ‰setter)ï¼Œä¸”è¯¥å‡½æ•°çš„å‚æ•°ä¸ªæ•°ä¸º2ï¼Œå³æ²¡æœ‰è¾“å…¥valçš„å®å‚
    val = obj[key]
  }

  let childOb = !shallow && observe(val) //ğŸ‘ˆ é€’å½’ã€‚æ·±åº¦ä¼˜å…ˆï¼Œå…ˆè§‚å¯Ÿå…¶å±æ€§å€¼ã€‚è‹¥è¯¥å±æ€§çš„å±æ€§å€¼ä¹Ÿä¸ºå¼•ç”¨ç±»å‹ï¼Œåˆ™å¾—åˆ°è¯¥å±æ€§å€¼çš„observer
  
  /*â— ä»¥ä¸‹æ˜¯æ•°æ®åŠ«æŒã€ä¾èµ–æ”¶é›†ã€æ´¾å‘æ›´æ–°çš„ä»£ç */
  /*â—                                     */
  Object.defineProperty(obj, key, {
    enumerable: true,
    configurable: true,
    get: function reactiveGetter () {
      const value = getter ? getter.call(obj) : val  //ğŸ‘ˆ å¦‚æœæœ‰getterï¼Œåˆ™æ‰§è¡Œç›¸åº”çš„getter
      if (Dep.target) {
        dep.depend() 
        if (childOb) { 
          childOb.dep.depend()  //ğŸ‘ˆ è¿™é‡Œçš„é€»è¾‘æ˜¯ï¼š å¦‚æœå¯¹è±¡açš„å±æ€§bçš„å±æ€§å€¼æ˜¯å¼•ç”¨ç±»å‹ï¼Œwatcherä¾èµ–äº†a.bï¼Œåˆ™ä¹Ÿä¾èµ–äº†a.bçš„å†…å®¹ â­
          if (Array.isArray(value)) {
            dependArray(value)
          }
        }
      }
      return value
    },
    set: function reactiveSetter (newVal) {
      const value = getter ? getter.call(obj) :  val
      /* eslint-disable no-self-compare */
      if (newVal === value || (newVal !== newVal && value !== value)) {  // ğŸ‘ˆ æ•°æ®æ²¡æœ‰å˜åŒ–ï¼Œä¸åšé¢å¤–èµ‹å€¼ï¼Œä¹Ÿä¸æ´¾å‘æ›´æ–°
        return
      }
      /* eslint-enable no-self-compare */
      if (process.env.NODE_ENV !== 'production' && customSetter) {
        customSetter()
      }
      // #7981: for accessor properties without setter
      if (getter && !setter) return
      if (setter) {
        setter.call(obj, newVal)
      } else {
        val = newVal
      }
      childOb = !shallow && observe(newVal)
      dep.notify()
    }
  })
}

/**
 * Set a property on an object. Adds the new property and
 * triggers change notification if the property doesn't
 * already exist.
 */
export function set (target: Array<any> | Object, key: any, val: any): any {
  if (process.env.NODE_ENV !== 'production' &&
    (isUndef(target) || isPrimitive(target))
  ) {
    warn(`Cannot set reactive property on undefined, null, or primitive value: ${(target: any)}`)
  }
  if (Array.isArray(target) && isValidArrayIndex(key)) {
    target.length = Math.max(target.length, key)
    target.splice(key, 1, val)
    return val
  }
  if (key in target && !(key in Object.prototype)) {
    target[key] = val
    return val
  }
  const ob = (target: any).__ob__
  if (target._isVue || (ob && ob.vmCount)) {
    process.env.NODE_ENV !== 'production' && warn(
      'Avoid adding reactive properties to a Vue instance or its root $data ' +
      'at runtime - declare it upfront in the data option.'
    )
    return val
  }
  if (!ob) {
    target[key] = val
    return val
  }
  defineReactive(ob.value, key, val)
  ob.dep.notify()
  return val
}

/**
 * Delete a property and trigger change if necessary.
 */
export function del (target: Array<any> | Object, key: any) {
  if (process.env.NODE_ENV !== 'production' &&
    (isUndef(target) || isPrimitive(target))
  ) {
    warn(`Cannot delete reactive property on undefined, null, or primitive value: ${(target: any)}`)
  }
  if (Array.isArray(target) && isValidArrayIndex(key)) {
    target.splice(key, 1)
    return
  }
  const ob = (target: any).__ob__
  if (target._isVue || (ob && ob.vmCount)) {
    process.env.NODE_ENV !== 'production' && warn(
      'Avoid deleting properties on a Vue instance or its root $data ' +
      '- just set it to null.'
    )
    return
  }
  if (!hasOwn(target, key)) {
    return
  }
  delete target[key]
  if (!ob) {
    return
  }
  ob.dep.notify()
}

/**
 * Collect dependencies on array elements when the array is touched, since
 * we cannot intercept array element access like property getters.
 */
function dependArray (value: Array<any>) {
  for (let e, i = 0, l = value.length; i < l; i++) {
    e = value[i]
    e && e.__ob__ && e.__ob__.dep.depend()
    if (Array.isArray(e)) {
      dependArray(e)
    }
  }
}
